import { Component } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-update-profile',
  standalone: true,
  imports: [ReactiveFormsModule, CommonModule],
  templateUrl: './update-profile.component.html',
  styleUrl: './update-profile.component.css'
})
export class UpdateProfileComponent {
  updateProfileForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.updateProfileForm = this.fb.group({
      countryOffice: ['Botswana', Validators.required],
      language: ['en', Validators.required]
    });
  }

  onSubmit() {
    if (this.updateProfileForm.valid) {
      console.log(this.updateProfileForm.value);
    }
  }
}
<div class="update-profile-container">
  <div class="header">
    <h2>Update Profile</h2>
  </div>
  <div class="logo-container">
    <img src="assets/logo.png" alt="tuneme logo" class="logo">
    <p class="tagline">dream, love & live it</p>
  </div>
  <form [formGroup]="updateProfileForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="countryOffice">Choose Country Office</label>
      <select id="countryOffice" formControlName="countryOffice">
        <option value="India">India</option>
        <option value="China">China</option>
        <option value="US">US</option>
        <option value="Botswana">Botswana</option>
      </select>
    </div>
    <div class="form-group">
      <label for="language">Choose Language</label>
      <select id="language" formControlName="language">
        <option value="en">en</option>
        <option value="fr">fr</option>
        <option value="gr">gr</option>
      </select>
    </div>
    <button type="submit" [disabled]="!updateProfileForm.valid">Continue</button>
  </form>
</div>
