import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

interface Slide {
  image: string;
  text1: string;
  text2: string;
}

@Component({
  selector: 'app-slider',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './slider.component.html',
  styleUrls: ['./slider.component.css']
})
export class SliderComponent implements OnInit {
  slides: Slide[] = [];
  currentIndex: number = 0;
  slidesPerPage: number = 3; // Number of slides to show at a time

  ngOnInit(): void {
    this.loadSlides();
  }

  async loadSlides(): Promise<void> {
    try {
      const response = await fetch('assets/slider-content.json');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      this.slides = await response.json();
    } catch (error) {
      console.error('Failed to load slider content:', error);
    }
  }

  nextSlide(): void {
    const maxIndex = this.slides.length - this.slidesPerPage;
    this.currentIndex = Math.min(this.currentIndex + this.slidesPerPage, maxIndex);
  }

  prevSlide(): void {
    this.currentIndex = Math.max(this.currentIndex - this.slidesPerPage, 0);
  }

  goToSlide(index: number): void {
    this.currentIndex = index * this.slidesPerPage;
  }

  get totalPages(): number {
    return Math.ceil(this.slides.length / this.slidesPerPage);
  }

  get currentDotIndex(): number {
    return Math.floor(this.currentIndex / this.slidesPerPage);
  }
}
