import { ApplicationConfig } from '@angular/core';
import { provideRouter, Routes } from '@angular/router';
import { provideHttpClient } from '@angular/common/http';
import { NAV_ITEMS, NavItem } from './shared/nav-items';

// Function to flatten NAV_ITEMS into a single array of Routes
function createRoutes(items: NavItem[]): Routes {
  let routes: Routes = [];
  for (const item of items) {
    if (item.route && item.component) {
      routes.push({
        path: item.route.startsWith('/') ? item.route.substring(1) : item.route,
        component: item.component,
      });
    }
    if (item.children) {
      routes.push(...createRoutes(item.children));
    }
  }
  return routes;
}

const appRoutes: Routes = [
  { path: '', redirectTo: 'home', pathMatch: 'full' },
  ...createRoutes(NAV_ITEMS),
  { path: '**', redirectTo: 'home' } // Wildcard route for a 404-like behavior
];

export const appConfig: ApplicationConfig = {
  providers: [
    provideRouter(appRoutes),
    provideHttpClient()
  ]
};
