import { Component, HostListener } from '@angular/core';
import { RouterOutlet } from '@angular/router'; // Import RouterOutlet
import { HeaderComponent } from './components/header/header.component';
import { FooterComponent } from './components/footer/footer.component';
import { DownloadSectionComponent } from './components/download-section/download-section.component';
import { LeftNavMenuComponent } from './components/left-nav-menu/left-nav-menu.component'; // Import LeftNavMenuComponent
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [HeaderComponent, FooterComponent, DownloadSectionComponent, LeftNavMenuComponent, RouterOutlet, CommonModule], // Add CommonModule
  template: `
    <app-header></app-header>
    <div class="main-container">
      <app-left-nav-menu></app-left-nav-menu>
      <div class="content-area">
        <router-outlet></router-outlet>
      </div>
    </div>
    <app-footer></app-footer>
    <button *ngIf="showBackToTop" class="back-to-top" (click)="scrollToTop()">
      Back to Top
    </button>
  `,
  styles: [`
    .main-container {
      display: flex;
      margin-top: 80px; /* Adjusted for fixed header clearance */
      min-height: calc(100vh - 140px); /* Adjust based on header and footer height */
    }

    .content-area {
      margin-left: 220px; /* Width of the sidebar */
      padding: 20px;
      flex-grow: 1;
    }

    .back-to-top {
      position: fixed;
      bottom: 20px;
      right: 20px;
      background-color: var(--primary-color);
      color: white;
      border: none;
      border-radius: 5px;
      padding: 10px 20px;
      cursor: pointer;
      z-index: 1000;
    }

    .back-to-top:hover {
      background-color: var(--primary-color-dark);
    }

    @media (max-width: 768px) {
      .main-container {
        margin-top: 0; /* Header will stack on mobile */
        flex-direction: column; /* Stack nav and content */
      }
      .content-area {
        margin-left: 0; /* No margin on mobile when sidebar is hidden */
      }
    }
  `]
})
export class AppComponent {
  title = 'gemini-angular-app';
  showBackToTop = false;

  @HostListener('window:scroll', [])
  onWindowScroll() {
    if (window.pageYOffset > 100) { // Show button after scrolling 100px
      this.showBackToTop = true;
    } else {
      this.showBackToTop = false;
    }
  }

  scrollToTop() {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }
}
