import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink, RouterLinkActive, Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

interface MenuItem {
  label: string;
  route: string;
  icon: string; // Class for Font Awesome icon
  roles?: string[]; // Optional: roles that can see this item
}

@Component({
  selector: 'app-left-nav-menu',
  standalone: true,
  imports: [CommonModule, RouterLink, RouterLinkActive],
  templateUrl: './left-nav-menu.component.html',
  styleUrl: './left-nav-menu.component.css'
})
export class LeftNavMenuComponent implements OnInit {
  menuItems: MenuItem[] = [
    { label: 'Home', route: '/home', icon: 'fas fa-home' },
    { label: 'My Knowledge', route: '/my-knowledge', icon: 'fas fa-book', roles: ['user', 'admin'] },
    { label: 'Find A Service', route: '/find-a-service', icon: 'fas fa-search', roles: ['user', 'admin'] },
    { label: 'My Health', route: '/my-health', icon: 'fas fa-heartbeat', roles: ['user', 'admin'] },
    { label: 'Your Tips', route: '/your-tips', icon: 'fas fa-lightbulb', roles: ['user', 'admin'] },
    { label: 'My Favourites', route: '/my-favourites', icon: 'fas fa-star', roles: ['user', 'admin'] },
  ];

  isMobileMenuOpen = false;
  currentUserRole: string = '';

  constructor(private authService: AuthService, private router: Router) { }

  ngOnInit(): void {
    this.currentUserRole = this.authService.getUserRole();
  }

  toggleMobileMenu() {
    this.isMobileMenuOpen = !this.isMobileMenuOpen;
  }

  shouldShowMenuItem(item: MenuItem): boolean {
    if (!item.roles) {
      return true; // Show if no specific roles are defined
    }
    return item.roles.includes(this.currentUserRole);
  }

  // Method to check if the current route matches the menu item's route
  isActive(route: string): boolean {
    return this.router.url === route;
  }
}
