import { Component } from '@angular/core';
import { HeaderComponent } from './components/header/header.component';
import { FooterComponent } from './components/footer/footer.component';
import { DownloadSectionComponent } from './components/download-section/download-section.component'; // Import DownloadSectionComponent

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [HeaderComponent, FooterComponent, DownloadSectionComponent], // Add DownloadSectionComponent to imports
  template: `<app-header></app-header><h1>Hello, Angular!</h1><app-download-section></app-download-section><app-footer></app-footer>`,
  styles: []
})
export class AppComponent {
  title = 'gemini-angular-app';
}
